// src/App.jsx
import React, { useState, useEffect } from 'react';

// --- COLOR MAP ---
const COLOR_MAP = {
  banking: { bg: 'bg-[#21436E]', text: 'text-white', iconBg: 'bg-[#21436E]', accent: 'text-indigo-300' },
  logistics: { bg: 'bg-gray-700', text: 'text-white', iconBg: 'bg-gray-700', accent: 'text-gray-300' },
};

// --- COURSE DATA ---
const COURSES_DATA = [
  { id: 'banking', title: 'Banking Specialists', logo: '💼', desc: 'Master communication for finance...', type: 'pro' },
  { id: 'logistics', title: 'Logistics Coordinator', logo: '🚚', desc: 'Master professional communication in logistics...', type: 'pro' },
];

// --- Banking Demo ---
const BankingDemoSimulatorPage = ({ course }) => {
  const [feedback, setFeedback] = useState(null);
  const chooseOption = (choice) => {
    const feedbacks = {
      restructure: { title: "Strategic and Client-Focused", text: "This is a strong leadership move...", style: "border-green-500 bg-green-100 text-green-900" },
      sell: { title: "Risk Aversion, at a Cost", text: "This action removes the risk...", style: "border-yellow-500 bg-yellow-100 text-yellow-900" },
      liquidate: { title: "Extreme, High-Impact Action", text: "This is a drastic measure...", style: "border-red-500 bg-red-100 text-red-900" },
      wait: { title: "Potentially Fatal Delay", text: "In a rapidly unfolding crisis...", style: "border-purple-500 bg-purple-100 text-purple-900" },
    };
    setFeedback(feedbacks[choice]);
  };
  return (
    <div className="flex items-center justify-center min-h-screen p-4" style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1579621970795-87facc2f976d)', backgroundSize: 'cover' }}>
      <div className="bg-gray-900/80 backdrop-blur-sm shadow-2xl rounded-3xl p-8 max-w-2xl w-full text-center text-white">
        <div className="bg-blue-800 rounded-t-3xl -mt-8 -mx-8 px-8 py-6 shadow-xl border-b-4 border-white">
          <h2 className="text-xl font-bold tracking-wide">Financial Portfolio Crisis</h2>
        </div>
        <div className="p-4 mt-6">
          <h1 className="text-3xl font-extrabold text-white mb-2">📊 Sudden Market Volatility</h1>
          <p className="text-lg text-gray-300 mb-6">You are the CFO...</p>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <button onClick={() => chooseOption('restructure')} className="w-full py-3 px-6 bg-green-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-green-700">🤝 Restructure client loans</button>
            <button onClick={() => chooseOption('sell')} className="w-full py-3 px-6 bg-yellow-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-yellow-700">📉 Sell off the high-risk portfolio</button>
            <button onClick={() => chooseOption('liquidate')} className="w-full py-3 px-6 bg-red-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-red-700">🚨 Immediately liquidate assets</button>
            <button onClick={() => chooseOption('wait')} className="w-full py-3 px-6 bg-purple-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-purple-700">⏱️ Wait for more data analysis</button>
          </div>
          {feedback && (
            <div className={`mt-8 p-6 rounded-2xl text-left shadow-xl border-l-4 ${feedback.style}`}>
              <h4 className="text-xl font-bold mb-2">💡 {feedback.title}</h4>
              <p className="font-semibold">{feedback.text}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// --- Logistics Demo ---
const LogisticsDemoSimulatorPage = ({ course }) => {
  const [feedback, setFeedback] = useState(null);
  const chooseOption = (choice) => {
    const feedbacks = {
      land_route: { title: "A Solid Contingency Plan", text: "This is a good, reliable choice...", style: "border-yellow-500 bg-yellow-100 text-yellow-900" },
      air_freight: { title: "The Best and Most Decisive Action", text: "This is the ideal solution...", style: "border-green-500 bg-green-100 text-green-900" },
      wait: { title: "A Catastrophic Failure of Leadership", text: "Doing nothing is an unacceptable failure...", style: "border-red-500 bg-red-100 text-red-900" },
      back_road: { title: "An Unnecessary Gamble", text: "Choosing a dangerous, unpaved road is reckless...", style: "border-purple-500 bg-purple-100 text-purple-900" },
    };
    setFeedback(feedbacks[choice]);
  };
  return (
    <div className="flex items-center justify-center min-h-screen p-4" style={{ backgroundImage: 'url(https://images.unsplash.com/photo-1549480016-d87d464b97e9)', backgroundSize: 'cover' }}>
      <div className="bg-gray-900/80 backdrop-blur-sm shadow-2xl rounded-3xl p-8 max-w-2xl w-full text-center text-white">
        <div className="bg-blue-800 rounded-t-3xl -mt-8 -mx-8 px-8 py-6 shadow-xl border-b-4 border-white">
          <h2 className="text-xl font-bold tracking-wide">Supply Chain Simulation</h2>
        </div>
        <div className="p-4 mt-6">
          <h1 className="text-3xl font-extrabold text-white mb-2">📦 Urgent Shipment Delayed</h1>
          <p className="text-lg text-gray-300 mb-6">You are a Logistics Manager...</p>
          <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
            <button onClick={() => chooseOption('land_route')} className="w-full py-3 px-6 bg-yellow-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-yellow-700">🛣️ Divert to Secondary Route</button>
            <button onClick={() => chooseOption('air_freight')} className="w-full py-3 px-6 bg-green-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-green-700">✈️ Arrange Emergency Air Freight</button>
            <button onClick={() => chooseOption('wait')} className="w-full py-3 px-6 bg-red-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-red-700">⏳ Wait for Highway to Clear</button>
            <button onClick={() => chooseOption('back_road')} className="w-full py-3 px-6 bg-blue-600 text-white font-bold rounded-xl text-lg shadow-lg hover:bg-blue-700">⚠️ Attempt Back Road</button>
          </div>
          {feedback && (
            <div className={`mt-8 p-6 rounded-2xl text-left shadow-xl border-l-4 ${feedback.style}`}>
              <h4 className="text-xl font-bold mb-2">💡 {feedback.title}</h4>
              <p className="font-semibold">{feedback.text}</p>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

// --- Placeholder Views for Pathway, Scenario, Unit 1 ---
const BankingPathwayView = () => <div className="p-8"><h2>🏦 Banking Pathway</h2></div>;
const BankingScenarioView = () => <div className="p-8"><h2>🏦 Banking Scenario</h2></div>;
const BankingUnit1View = () => <div className="p-8"><h2>🏦 Banking Unit 1</h2></div>;

const LogisticsPathwayView = () => <div className="p-8"><h2>🚚 Logistics Pathway</h2></div>;
const LogisticsScenarioView = () => <div className="p-8"><h2>🚚 Logistics Scenario</h2></div>;
const LogisticsUnit1View = () => <div className="p-8"><h2>🚚 Logistics Unit 1</h2></div>;

// --- Exam Simulators (minimal) ---
const BankingExamSimulatorPage = () => <div className="p-8"><h2>🏦 Banking Exam</h2></div>;
const LogisticsExamSimulatorPage = () => <div className="p-8"><h2>🚚 Logistics Exam</h2></div>;

// --- Course Details Page ---
const CourseDetailsPage = ({ activeCourse, setCurrentPage }) => {
  const [tab, setTab] = useState('Pathway');
  const renderContent = () => {
    if (activeCourse.id === 'banking') {
      switch (tab) {
        case 'Pathway': return <BankingPathwayView />;
        case 'Scenario': return <BankingScenarioView />;
        case 'Unit 1': return <BankingUnit1View />;
        default: return <BankingPathwayView />;
      }
    } else if (activeCourse.id === 'logistics') {
      switch (tab) {
        case 'Pathway': return <LogisticsPathwayView />;
        case 'Scenario': return <LogisticsScenarioView />;
        case 'Unit 1': return <LogisticsUnit1View />;
        default: return <LogisticsPathwayView />;
      }
    }
    return <div>Content</div>;
  };
  return (
    <div className="p-6 max-w-6xl mx-auto">
      <h1 className="text-3xl font-bold mb-4">{activeCourse.title}</h1>
      <div className="flex gap-2 mb-6">
        {['Pathway', 'Scenario', 'Unit 1'].map(t => (
          <button key={t} onClick={() => setTab(t)} className={`px-4 py-2 rounded ${tab === t ? 'bg-indigo-600 text-white' : 'bg-gray-200'}`}>
            {t}
          </button>
        ))}
      </div>
      <div className="bg-gray-100 p-6 rounded-lg min-h-[400px]">
        {renderContent()}
      </div>
      <div className="mt-6 flex gap-4">
        <button onClick={() => setCurrentPage('PricingPlans')} className="px-6 py-3 bg-red-600 text-white rounded-lg">ENROLL NOW</button>
        <button onClick={() => setCurrentPage('DemoQuizForm')} className="px-6 py-3 bg-yellow-500 text-gray-900 rounded-lg">Demo Sim</button>
        <button onClick={() => setCurrentPage('ExamSimulator')} className="px-6 py-3 bg-blue-500 text-white rounded-lg">Exam Sim</button>
      </div>
    </div>
  );
};

// --- Course Card ---
const CourseCard = ({ course, setActiveCourse, setCurrentPage }) => {
  const color = COLOR_MAP[course.id] || {};
  const handle = (action) => {
    setActiveCourse(course);
    if (action === 'ENROLL') {
      setCurrentPage('PricingPlans');
    } else if (action === 'Demo Simulator') {
      setCurrentPage('DemoQuizForm');
    } else if (action === 'Exam Simulator') {
      setCurrentPage('ExamSimulator');
    } else {
      setActiveCourse({ ...course, initialTab: action });
      setCurrentPage('CourseDetails');
    }
  };
  return (
    <div className="bg-white p-5 rounded-xl shadow-lg">
      <div className={`w-14 h-14 mx-auto mb-4 rounded-full text-2xl text-white flex items-center justify-center ${color.iconBg}`}>{course.logo}</div>
      <h3 className="text-xl font-bold text-center">{course.title}</h3>
      <p className="text-sm text-gray-500 text-center mb-4">{course.desc}</p>
      <div className="space-y-2">
        <div className="flex justify-between">
          <button onClick={() => handle('Demo Simulator')} className="text-xs bg-yellow-100 px-2 py-1 rounded">▶️ Demo</button>
          <button onClick={() => handle('Exam Simulator')} className="text-xs bg-blue-100 px-2 py-1 rounded">📝 Exam</button>
        </div>
        <div className="grid grid-cols-3 gap-1">
          <button onClick={() => handle('Pathway')} className="text-xs bg-indigo-100 px-2 py-1 rounded">📘 Pathway</button>
          <button onClick={() => handle('Scenario')} className="text-xs bg-green-100 px-2 py-1 rounded">🧩 Scenario</button>
          <button onClick={() => handle('Unit 1')} className="text-xs bg-purple-100 px-2 py-1 rounded">🧪 Unit 1</button>
        </div>
        <button onClick={() => handle('ENROLL')} className="w-full mt-2 py-2 bg-red-600 text-white text-sm font-bold rounded">💰 ENROLL NOW</button>
      </div>
    </div>
  );
};

// --- Main Pages ---
const HomePage = ({ setActiveCourse, setCurrentPage }) => (
  <div className="p-8">
    <header className="text-center mb-10">
      <h1 className="text-4xl font-bold">🎓 Aulice ProMastery Portal</h1>
    </header>
    <div className="grid grid-cols-1 md:grid-cols-2 gap-6 max-w-6xl mx-auto">
      {COURSES_DATA.map(course => (
        <CourseCard key={course.id} course={course} setActiveCourse={setActiveCourse} setCurrentPage={setCurrentPage} />
      ))}
    </div>
  </div>
);

const DemoQuizFormPage = ({ activeCourse }) => {
  if (activeCourse?.id === 'logistics') return <LogisticsDemoSimulatorPage course={activeCourse} />;
  return <BankingDemoSimulatorPage course={activeCourse} />;
};

const ExamSimulatorPage = ({ activeCourse }) => {
  if (activeCourse?.id === 'logistics') return <LogisticsExamSimulatorPage />;
  return <BankingExamSimulatorPage />;
};

const PricingPlansPage = () => <div className="p-8"><h2>💰 Pricing Plans</h2></div>;

// --- Main App ---
export default function App() {
  const [currentPage, setCurrentPage] = useState('Home');
  const [activeCourse, setActiveCourse] = useState(null);

  const renderPage = () => {
    switch (currentPage) {
      case 'CourseDetails': return <CourseDetailsPage activeCourse={activeCourse} setCurrentPage={setCurrentPage} />;
      case 'DemoQuizForm': return <DemoQuizFormPage activeCourse={activeCourse} />;
      case 'ExamSimulator': return <ExamSimulatorPage activeCourse={activeCourse} />;
      case 'PricingPlans': return <PricingPlansPage />;
      default: return <HomePage setActiveCourse={setActiveCourse} setCurrentPage={setCurrentPage} />;
    }
  };

  return (
    <div className="min-h-screen bg-gray-50">
      <nav className="bg-[#0056b3] text-white p-4">
        <button onClick={() => setCurrentPage('Home')} className="font-bold">Aulice</button>
      </nav>
      <main>{renderPage()}</main>
    </div>
  );
}
