// src/App.jsx
import React, { useState } from 'react';

// Import page components
import HomePage from './components/HomePage';
import BankingDemoSimulatorPage from './pages/BankingDemoSimulatorPage';
import LogisticsDemoSimulatorPage from './pages/LogisticsDemoSimulatorPage';
import ExamSimulatorPage from './pages/ExamSimulatorPage';
import PathwayPage from './pages/PathwayPage';
import ScenarioPage from './pages/ScenarioPage';
import Unit1Page from './pages/Unit1Page';
import PricingOptionsPage from './pages/PricingOptionsPage';

// Placeholder pages (you can replace these later)
const YoungGeniusQuizPage = () => <div className="p-8"><h1>Young Genius Quiz</h1></div>;
const CustomTrackFormPage = () => <div className="p-8"><h1>Custom Track Form</h1></div>;
const YoungGeniusFormPage = () => <div className="p-8"><h1>Young Genius Form</h1></div>;

export default function App() {
  const [page, setPage] = useState('home');
  const [activeCourse, setActiveCourse] = useState(null);

  const navigate = (newPage) => {
    setPage(newPage);
  };

  const renderPage = () => {
    switch (page) {
      case 'quiz':
        return <YoungGeniusQuizPage />;
      case 'customTrackForm':
        return <CustomTrackFormPage />;
      case 'geniusForm':
        return <YoungGeniusFormPage />;
      case 'DemoSimulator':
        if (activeCourse?.id === 'logistics') {
          return <LogisticsDemoSimulatorPage course={activeCourse} />;
        }
        return <BankingDemoSimulatorPage course={activeCourse} />;
      case 'ExamSimulator':
        return <ExamSimulatorPage course={activeCourse} />;
      case 'Pathway':
        return <PathwayPage course={activeCourse} />;
      case 'Scenario':
        return <ScenarioPage course={activeCourse} />;
      case 'Unit1':
        return <Unit1Page course={activeCourse} />;
      case 'PricingPlans':
        return <PricingOptionsPage course={activeCourse} />;
      default:
        return <HomePage setCurrentPage={navigate} setActiveCourse={setActiveCourse} />;
    }
  };

  // Show "Back to Home" only on non-home, non-fullscreen pages
  const showBackButton = !['home', 'DemoSimulator', 'ExamSimulator'].includes(page);

  return (
    <main className="min-h-screen bg-gray-50 font-sans">
      {showBackButton && (
        <button
          onClick={() => navigate('home')}
          className="absolute top-4 left-4 z-10 bg-white hover:bg-gray-200 text-gray-800 font-bold py-2 px-4 rounded-lg transition-colors shadow-md"
        >
          &larr; Back to Home
        </button>
      )}
      {renderPage()}
    </main>
  );
}
